import React, { useState } from 'react';
import { Box, Typography, Button, TextField, FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

function TrainerDashboard({ trainers = [], clients = [], chat = [], sendMessage, setMessage, handleTyping, message, typingUser, selectedRecipient, setSelectedRecipient }) {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [description, setDescription] = useState('');

  return (
    <Box mt={3}>
      <Typography variant="h6">📅 לוח שנה</Typography>
      <Calendar onChange={setSelectedDate} value={selectedDate} />
      <TextField label="תיאור" value={description} onChange={(e) => setDescription(e.target.value)} fullWidth sx={{ mt: 2 }} />
      <Button variant="contained" sx={{ mt: 2 }}>הוסף תזכורת</Button>

      <Box sx={{ mt: 3 }}>
        <Typography variant="h6">💬 צ'אט</Typography>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>בחר משתמש/קבוצה</InputLabel>
          <Select value={selectedRecipient} onChange={(e) => setSelectedRecipient(e.target.value)}>
            {clients.map((client) => (
              <MenuItem key={client.username} value={client.username}>🐶 {client.username} (לקוח)</MenuItem>
            ))}
          </Select>
        </FormControl>
        <TextField value={message} onChange={(e) => { setMessage(e.target.value); handleTyping(); }} label="הקלד הודעה" fullWidth />
        <Button onClick={sendMessage} variant="contained" sx={{ mt: 1 }}>שלח</Button>
        {typingUser && <Typography sx={{ color: "gray", mt: 1 }}>{typingUser} מקליד...</Typography>}
        <ul>
          {chat.map((msg, index) => (
            <li key={index}>{msg.user}: {msg.text}</li>
          ))}
        </ul>
      </Box>
    </Box>
  );
}

export default TrainerDashboard;
