import React from "react";
import { Paper, Box, Typography, IconButton } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { ArrowBackIos, ArrowForwardIos } from "@mui/icons-material";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  return (
    <Paper
      sx={{
        flex: 1,
        maxWidth: "600px", // üìå ◊û◊®◊ó◊ô◊ë ◊ê◊™ ◊î◊ú◊ï◊ó
        height: "450px", // üìå ◊í◊ï◊ë◊î ◊ê◊ó◊ô◊ì
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "space-between",
        borderRadius: "16px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      {/* ‚úÖ ◊õ◊ï◊™◊®◊™ ◊¢◊ù ◊†◊ô◊ï◊ï◊ò */}
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
        }}
      >
        <IconButton onClick={() => setSelectedDate(new Date(selectedDate.setMonth(selectedDate.getMonth() - 1)))}>
          <ArrowBackIos />
        </IconButton>
        <Typography variant="h6" sx={{ fontWeight: "bold" }}>
          {selectedDate.toLocaleString("he-IL", { month: "long", year: "numeric" })}
        </Typography>
        <IconButton onClick={() => setSelectedDate(new Date(selectedDate.setMonth(selectedDate.getMonth() + 1)))}>
          <ArrowForwardIos />
        </IconButton>
      </Box>

      {/* ‚úÖ ◊î◊ú◊ï◊ó ◊¢◊¶◊û◊ï - ◊ô◊û◊ú◊ê ◊ê◊™ ◊õ◊ú ◊î◊ß◊ï◊§◊°◊î */}
      <Box sx={{ flexGrow: 1, width: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          tileClassName="custom-calendar-tile"
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
