import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, AppBar, Toolbar, Container, CircularProgress, FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [description, setDescription] = useState('');
  const [trainers, setTrainers] = useState([]);
  const [clients, setClients] = useState([]);
  const [selectedRecipient, setSelectedRecipient] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUser, setTypingUser] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    const savedRole = localStorage.getItem("role");
    if (savedUser && savedRole) {
      setUser(JSON.parse(savedUser));
      setRole(savedRole);
    }

    const fetchData = async () => {
      try {
        const [messagesRes, tasksRes, appointmentsRes, trainersRes, clientsRes] = await Promise.all([
          axios.get('http://localhost:5000/chat/messages'),
          axios.get('http://localhost:5000/tasks'),
          axios.get('http://localhost:5000/appointments', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }),
          axios.get('http://localhost:5000/trainers', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }),
          axios.get('http://localhost:5000/clients', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }),
        ]);

        setChat(messagesRes.data);
        setTasks(tasksRes.data);
        setAppointments(appointmentsRes.data);
        setTrainers(trainersRes.data);
        setClients(clientsRes.data);
      } catch (error) {
        console.error("🔴 שגיאה בטעינת הנתונים:", error);
      }
    };

    fetchData();

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    socket.on("userTyping", (data) => {
      setTypingUser(data.user);
      setTimeout(() => setTypingUser(null), 3000);
    });

  }, []);

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      setUser(response.data.user);
      setRole(response.data.user.role);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));
      localStorage.setItem('role', response.data.user.role);
    } catch (error) {
      console.error('🔴 שגיאה בהתחברות:', error);
      alert('שם משתמש או סיסמה שגויים');
    }
  };

  const handleTyping = () => {
    socket.emit("typing", { user: user.username });
  };

  const sendMessage = () => {
    if (!selectedRecipient) {
      alert("⚠ יש לבחור משתמש או קבוצה לשליחת ההודעה!");
      return;
    }
    socket.emit('sendMessage', { 
      user: user.username, 
      text: message, 
      recipient: selectedRecipient
    });
    setMessage('');
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>🐾 מערכת ניהול מאלפים</Typography>
          <Button color="inherit">דף הבית</Button>
          <Button color="inherit">פורום</Button>
          <Button color="inherit">מצא מאלף</Button>
          {user && (
            <>
              <Typography variant="h6" sx={{ mx: 2 }}>שלום, {user.username}</Typography>
              <Button color="inherit" onClick={() => { 
                setUser(null); 
                setRole(null); 
                localStorage.removeItem('token'); 
                localStorage.removeItem('user'); 
                localStorage.removeItem('role'); 
              }}>
                התנתק
              </Button>
            </>
          )}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <TextField label="שם משתמש" value={username} onChange={(e) => setUsername(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <TextField 
            label="סיסמה" 
            type={showPassword ? "text" : "password"} 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            fullWidth sx={{ mb: 2 }} 
            InputProps={{
              endAdornment: (
                <Button onClick={() => setShowPassword(!showPassword)}>
                  {showPassword ? "🔒" : "👁"}
                </Button>
              ),
            }}
          />
          <Button variant="contained" onClick={login}>התחבר</Button>
        </Box>
      ) : (
        <>
          {role !== "admin" && (
            <Box mt={3}>
              <Typography variant="h6">💬 צ'אט</Typography>

              <FormControl fullWidth sx={{ mb: 2 }}>
                <InputLabel>בחר משתמש/קבוצה</InputLabel>
                <Select value={selectedRecipient} onChange={(e) => setSelectedRecipient(e.target.value)}>
                  {trainers.map((trainer) => (
                    <MenuItem key={trainer.username} value={trainer.username}>👨‍🏫 {trainer.username} (מאלף)</MenuItem>
                  ))}
                  {clients.map((client) => (
                    <MenuItem key={client.username} value={client.username}>🐶 {client.username} (לקוח)</MenuItem>
                  ))}
                </Select>
              </FormControl>

              <TextField value={message} onChange={(e) => { setMessage(e.target.value); handleTyping(); }} label="הקלד הודעה" fullWidth />
              <Button onClick={sendMessage} variant="contained" sx={{ mt: 1 }}>שלח</Button>
              {typingUser && <Typography sx={{ color: "gray", mt: 1 }}>{typingUser} מקליד...</Typography>}
              <ul>
                {chat.map((msg, index) => (
                  <li key={index}>{msg.user}: {msg.text}</li>
                ))}
              </ul>
            </Box>
          )}
        </>
      )}
    </Container>
  );
}

export default App;
