import React, { useState, useEffect } from "react";
import { Box, useTheme } from "@mui/material";
import CalendarWidget from "./components/CalendarWidget";
import TaskList from "./components/TaskList";
import axios from "axios";

const TrainerDashboard = () => {
  const theme = useTheme();
  const isRTL = theme.direction === "rtl";

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [tasks, setTasks] = useState([]);

  // טעינת משימות מהשרת
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get("http://localhost:5000/tasks", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });
        setTasks(response.data);
      } catch (error) {
        console.error("❌ שגיאה בטעינת משימות:", error);
      }
    };
    fetchTasks();
  }, []);

  // הוספת משימה חדשה
  const addTask = async (taskDescription) => {
    if (!taskDescription.trim()) return;
    try {
      const response = await axios.post(
        "http://localhost:5000/tasks",
        { description: taskDescription, assignedTo: "trainer" }, 
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        }
      );
      setTasks([...tasks, response.data]); // עדכון רשימת המשימות
    } catch (error) {
      console.error("❌ שגיאה בהוספת משימה:", error);
    }
  };

  return (
    <Box display="flex" flexDirection={isRTL ? "row-reverse" : "row"} width="100%">
      {/* ✅ לוח שנה */}
      <CalendarWidget selectedDate={selectedDate} setSelectedDate={setSelectedDate} />

      {/* ✅ רשימת משימות */}
      <TaskList tasks={tasks} addTask={addTask} />
    </Box>
  );
};

export default TrainerDashboard;
