import React, { useState } from "react";
import { Paper, Box, Button } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  const handleDateClick = (date) => {
    if (!startDate || (startDate && endDate)) {
      setStartDate(date);
      setEndDate(null);
    } else if (startDate && !endDate) {
      if (date < startDate) {
        setEndDate(startDate);
        setStartDate(date);
      } else {
        setEndDate(date);
      }
    }
  };

  const handleTodayClick = () => {
    const today = new Date();
    setSelectedDate(today);
    setStartDate(null);
    setEndDate(null);
  };

  return (
    <Paper
      sx={{
        flex: 1,
        width: "100%",
        height: "420px",
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "12px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      <Box sx={{ width: "100%", display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "10px" }}>
        <Button variant="contained" color="primary" onClick={handleTodayClick}>
          ◊ó◊ñ◊®◊î ◊ú◊î◊ô◊ï◊ù üìÖ
        </Button>
      </Box>

      <Box sx={{ width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          onClickDay={handleDateClick}
          tileClassName={({ date }) => {
            if (startDate && endDate && date >= startDate && date <= endDate) {
              return "highlight-range";
            }
            return null;
          }}
          className="custom-calendar"
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
