import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, AppBar, Toolbar, Container, CircularProgress, FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const socket = io('http://localhost:5000');

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [description, setDescription] = useState('');
  const [trainers, setTrainers] = useState([]);
  const [clients, setClients] = useState([]);
  const [selectedRecipient, setSelectedRecipient] = useState('');
  const [originalUser, setOriginalUser] = useState(null);
  const [isTyping, setIsTyping] = useState(false);
  const [typingUser, setTypingUser] = useState(null);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:5000/chat/messages');
        setChat(response.data);
      } catch (error) {
        console.error("üî¥ ◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊§◊™ ◊î◊ï◊ì◊¢◊ï◊™:", error);
      }
    };

    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error("üî¥ ◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊§◊™ ◊û◊©◊ô◊û◊ï◊™:", error);
      }
    };

    const fetchAppointments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/appointments', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setAppointments(response.data);
      } catch (error) {
        console.error("üî¥ ◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊§◊™ ◊™◊ñ◊õ◊ï◊®◊ï◊™:", error);
      }
    };

    const fetchTrainersAndClients = async () => {
      try {
        const trainersResponse = await axios.get('http://localhost:5000/trainers', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setTrainers(trainersResponse.data);

        const clientsResponse = await axios.get('http://localhost:5000/clients', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setClients(clientsResponse.data);
      } catch (error) {
        console.error("üî¥ ◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊§◊™ ◊û◊©◊™◊û◊©◊ô◊ù:", error);
      }
    };

    fetchMessages();
    fetchTasks();
    if (role !== 'admin') {
      fetchAppointments();
    }
    if (role === 'admin' || role === 'trainer') {
      fetchTrainersAndClients();
    }

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    socket.on("userTyping", (data) => {
      setTypingUser(data.user);
      setTimeout(() => setTypingUser(null), 3000);
    });

  }, [role]);

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      setUser(response.data.user);
      setRole(response.data.user.role);
      localStorage.setItem('token', response.data.token);
    } catch (error) {
      console.error('üî¥ ◊©◊í◊ô◊ê◊î ◊ë◊î◊™◊ó◊ë◊®◊ï◊™:', error);
      alert('◊©◊ù ◊û◊©◊™◊û◊© ◊ê◊ï ◊°◊ô◊°◊û◊î ◊©◊í◊ï◊ô◊ô◊ù');
    }
  };

  const handleTyping = () => {
    socket.emit("typing", { user: user.username });
  };

  const sendMessage = () => {
    if (!selectedRecipient) {
      alert("‚ö† ◊ô◊© ◊ú◊ë◊ó◊ï◊® ◊û◊©◊™◊û◊© ◊ê◊ï ◊ß◊ë◊ï◊¶◊î ◊ú◊©◊ú◊ô◊ó◊™ ◊î◊î◊ï◊ì◊¢◊î!");
      return;
    }
    socket.emit('sendMessage', { 
      user: user.username, 
      text: message, 
      recipient: selectedRecipient
    });
    setMessage('');
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>üêæ ◊û◊¢◊®◊õ◊™ ◊†◊ô◊î◊ï◊ú ◊û◊ê◊ú◊§◊ô◊ù</Typography>
          <Button color="inherit">◊ì◊£ ◊î◊ë◊ô◊™</Button>
          <Button color="inherit">◊§◊ï◊®◊ï◊ù</Button>
          <Button color="inherit">◊û◊¶◊ê ◊û◊ê◊ú◊£</Button>
          {user ? (
            <>
              <Typography variant="h6" sx={{ mx: 2 }}>◊©◊ú◊ï◊ù, {user.username}</Typography>
              <Button color="inherit" onClick={() => { setUser(null); setRole(null); localStorage.removeItem('token'); }}>
                ◊î◊™◊†◊™◊ß
              </Button>
            </>
          ) : (
            <Button color="inherit" onClick={login}>◊î◊™◊ó◊ë◊®</Button>
          )}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <TextField label="◊©◊ù ◊û◊©◊™◊û◊©" value={username} onChange={(e) => setUsername(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <TextField label="◊°◊ô◊°◊û◊î" type="password" value={password} onChange={(e) => setPassword(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <Button variant="contained" onClick={login}>◊î◊™◊ó◊ë◊®</Button>
        </Box>
      ) : (
        <Box mt={3}>
          <Box sx={{ mt: 3, p: 2, borderTop: "1px solid #ccc" }}>
            <Typography variant="h6">üí¨ ◊¶'◊ê◊ò</Typography>

            <FormControl fullWidth sx={{ mb: 2 }}>
              <InputLabel>◊ë◊ó◊® ◊û◊©◊™◊û◊©/◊ß◊ë◊ï◊¶◊î</InputLabel>
              <Select value={selectedRecipient} onChange={(e) => setSelectedRecipient(e.target.value)}>
                {trainers.map((trainer) => (
                  <MenuItem key={trainer.username} value={trainer.username}>üë®‚Äçüè´ {trainer.username} (◊û◊ê◊ú◊£)</MenuItem>
                ))}
                {clients.map((client) => (
                  <MenuItem key={client.username} value={client.username}>üê∂ {client.username} (◊ú◊ß◊ï◊ó)</MenuItem>
                ))}
              </Select>
            </FormControl>

            <TextField 
              value={message} 
              onChange={(e) => { setMessage(e.target.value); handleTyping(); }} 
              label="◊î◊ß◊ú◊ì ◊î◊ï◊ì◊¢◊î" 
              fullWidth 
            />
            <Button onClick={sendMessage} variant="contained" sx={{ mt: 1 }}>◊©◊ú◊ó</Button>
            {typingUser && <Typography sx={{ color: "gray", mt: 1 }}>{typingUser} ◊û◊ß◊ú◊ô◊ì...</Typography>}
            <ul>
              {chat.map((msg, index) => (
                <li key={index}>{msg.user}: {msg.text}</li>
              ))}
            </ul>
          </Box>
        </Box>
      )}
    </Container>
  );
}

export default App;
