import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, Paper, AppBar, Toolbar, Tabs, Tab, Container } from '@mui/material';

const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [tabIndex, setTabIndex] = useState(0); // ניווט בין כרטיסיות

  useEffect(() => {
    const fetchMessages = async () => {
      const response = await axios.get('http://localhost:5000/chat/messages');
      setChat(response.data);
    };

    const fetchTasks = async () => {
      const response = await axios.get('http://localhost:5000/tasks');
      setTasks(response.data);
    };

    fetchMessages();
    fetchTasks();

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

  }, []);


  const login = async () => {
    const response = await axios.post('http://localhost:5000/login', { username: 'testUser', password: '123456' });
    setUser(response.data.user);
  };

  const sendMessage = () => {
    socket.emit('sendMessage', { user: user.username, text: message });
    setMessage('');
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            מערכת ניהול משתמשים
          </Typography>
          {user && <Typography variant="h6">שלום, {user.username}</Typography>}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <Button variant="contained" onClick={login}>
            התחבר
          </Button>
        </Box>
      ) : (
        <Box mt={3}>
          <Tabs value={tabIndex} onChange={(e, newValue) => setTabIndex(newValue)} centered>
            <Tab label="צ'אט" />
            <Tab label="ניהול משימות" />
            <Tab label="קבצים" />
          </Tabs>

          <Paper sx={{ p: 3, mt: 3 }}>
            {tabIndex === 0 && (
              <>
                <Typography variant="h6">צ'אט</Typography>
                <TextField value={message} onChange={(e) => setMessage(e.target.value)} label="הקלד הודעה" fullWidth />
                <Button onClick={sendMessage} variant="contained" sx={{ mt: 1 }}>
                  שלח
                </Button>
                <ul>
                  {chat.map((msg, index) => (
                    <li key={index}>{msg.user}: {msg.text}</li>
                  ))}
                </ul>
              </>
            )}

            {tabIndex === 1 && <Typography variant="h6">ניהול משימות (כאן יוצג רשימת משימות)</Typography>}
            {tabIndex === 2 && <Typography variant="h6">ניהול קבצים (כאן תתאפשר העלאת קבצים)</Typography>}
          </Paper>
        </Box>
      )}
    </Container>
  );
}

export default App;
