import React, { useState } from "react";
import { Paper, Box, Button } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [viewDate, setViewDate] = useState(new Date()); // ◊©◊ï◊ú◊ò ◊ë◊™◊¶◊ï◊í◊î ◊©◊ú ◊î◊ó◊ï◊ì◊©

  const handleDateClick = (date) => {
    if (!startDate || (startDate && endDate)) {
      setStartDate(date);
      setEndDate(null);
    } else if (startDate && !endDate) {
      if (date < startDate) {
        setEndDate(startDate);
        setStartDate(date);
      } else {
        setEndDate(date);
      }
    }
  };

  const handleTodayClick = () => {
    const today = new Date();
    setSelectedDate(today);
    setStartDate(null);
    setEndDate(null);
    setViewDate(today); // ◊û◊¢◊ì◊õ◊ü ◊ê◊™ ◊î◊ó◊ï◊ì◊© ◊ú◊™◊¶◊ï◊í◊î ◊î◊†◊õ◊ï◊†◊î
  };

  return (
    <Paper
      sx={{
        flex: 1,
        width: "100%",
        height: "420px",
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "12px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      {/* ‚úÖ ◊©◊ï◊®◊™ ◊î◊õ◊ï◊™◊®◊™ ◊î◊ô◊®◊ï◊ß◊î */}
      <Box sx={{ width: "100%", display: "flex", justifyContent: "space-between", alignItems: "center", background: "#4CAF50", color: "#fff", padding: "10px", borderRadius: "8px 8px 0 0" }}>
        <span>üìÖ {viewDate.toLocaleString("he-IL", { month: "long", year: "numeric" })}</span>
        <Button variant="contained" color="secondary" onClick={handleTodayClick}>
          ◊ó◊ñ◊®◊î ◊ú◊î◊ô◊ï◊ù
        </Button>
      </Box>

      <Box sx={{ width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          onClickDay={handleDateClick}
          tileClassName={({ date }) => {
            if (startDate && endDate && date >= startDate && date <= endDate) {
              return "highlight-range";
            }
            return null;
          }}
          className="custom-calendar"
          activeStartDate={viewDate} // ◊û◊ë◊ò◊ô◊ó ◊©◊î◊™◊¶◊ï◊í◊î ◊™◊™◊¢◊ì◊õ◊ü ◊ë◊î◊™◊ê◊ù
          onActiveStartDateChange={({ activeStartDate }) => setViewDate(activeStartDate)} // ◊¢◊ï◊ß◊ë ◊ê◊ó◊®◊ô ◊©◊ô◊†◊ï◊ô◊ô ◊ó◊ï◊ì◊©
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
