import React, { useState } from "react";
import { Paper, Box, Button, IconButton } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css";
import { ArrowBackIos, ArrowForwardIos } from "@mui/icons-material";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  const [currentDate, setCurrentDate] = useState(new Date());

  const handleTodayClick = () => {
    setSelectedDate(new Date());
  };

  const handlePrevMonth = () => {
    const newDate = new Date(selectedDate);
    newDate.setMonth(newDate.getMonth() - 1);
    setSelectedDate(newDate);
  };

  const handleNextMonth = () => {
    const newDate = new Date(selectedDate);
    newDate.setMonth(newDate.getMonth() + 1);
    setSelectedDate(newDate);
  };

  return (
    <Paper
      sx={{
        flex: 1,
        width: "100%",
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "12px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      {/* סרגל עליון עם שם החודש, כפתורי ניווט וכפתור חזרה להיום */}
      <Box
        sx={{
          width: "100%",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          padding: "8px",
          background: "#4CAF50",
          color: "#fff",
          borderRadius: "8px",
        }}
      >
        <IconButton onClick={handlePrevMonth} sx={{ color: "#fff" }}>
          <ArrowBackIos />
        </IconButton>

        <h3 style={{ margin: 0 }}>
          {selectedDate.toLocaleString("he-IL", { month: "long", year: "numeric" })}
        </h3>

        <Button
          onClick={handleTodayClick}
          variant="contained"
          color="secondary"
          sx={{ fontSize: "12px", padding: "5px 10px" }}
        >
          חזרה להיום
        </Button>

        <IconButton onClick={handleNextMonth} sx={{ color: "#fff" }}>
          <ArrowForwardIos />
        </IconButton>
      </Box>

      {/* לוח השנה */}
      <Box sx={{ width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          locale="he-IL"
          showNavigation={false}
          calendarType="hebrew"
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
