import React, { useState } from "react";
import { Paper, Box, Button } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  const [value, setValue] = useState(new Date());

  return (
    <Paper
      sx={{
        flex: 1,
        width: "100%",
        height: "400px",
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "12px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      {/* ✅ סרגל הכותרת עם כפתור "חזרה להיום" */}
      <Box sx={{ width: "100%", display: "flex", justifyContent: "space-between", alignItems: "center", backgroundColor: "#4CAF50", color: "#fff", padding: "5px 15px", borderRadius: "10px 10px 0 0" }}>
        <span>{value.toLocaleDateString("he-IL", { year: "numeric", month: "long" })}</span>
        <Button
          onClick={() => setValue(new Date())}
          variant="contained"
          sx={{ backgroundColor: "#9C27B0", color: "#fff", fontSize: "12px", padding: "5px 10px" }}
        >
          חזרה להיום
        </Button>
      </Box>

      {/* ✅ לוח שנה */}
      <Box sx={{ width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setValue}
          value={value}
          locale="he-IL"
          firstDayOfWeek={0} // קובע שיום ראשון יהיה היום הראשון בלוח שנה
          formatShortWeekday={(locale, date) =>
            new Intl.DateTimeFormat("he-IL", { weekday: "short" }).format(date)
          }
          className="custom-calendar"
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
