import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, AppBar, Toolbar, Container } from '@mui/material';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [description, setDescription] = useState('');

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:5000/chat/messages');
        setChat(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת הודעות:", error);
      }
    };

    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת משימות:", error);
      }
    };

    const fetchAppointments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/appointments', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setAppointments(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת תזכורות:", error);
      }
    };

    fetchMessages();
    fetchTasks();
    if (role !== 'admin') {
      fetchAppointments();
    }

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

  }, [role]);

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      setUser(response.data.user);
      setRole(response.data.user.role);
      localStorage.setItem('token', response.data.token);
    } catch (error) {
      console.error('🔴 שגיאה בהתחברות:', error);
      alert('שם משתמש או סיסמה שגויים');
    }
  };

  const sendMessage = () => {
    socket.emit('sendMessage', { user: user.username, text: message });
    setMessage('');
  };

  const addAppointment = async () => {
    if (role !== 'trainer') return;
    try {
      await axios.post('http://localhost:5000/appointments', {
        date: selectedDate,
        client: user.username,
        description
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setAppointments([...appointments, { date: selectedDate, client: user.username, description }]);
      setDescription('');
    } catch (error) {
      console.error("🔴 שגיאה בהוספת תזכורת:", error);
    }
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>🐾 מערכת ניהול מאלפים</Typography>
          <Button color="inherit">דף הבית</Button>
          <Button color="inherit">פורום</Button>
          <Button color="inherit">מצא מאלף</Button>
          {user ? (
            <>
              <Typography variant="h6" sx={{ mx: 2 }}>שלום, {user.username}</Typography>
              <Button color="inherit" onClick={() => { setUser(null); setRole(null); localStorage.removeItem('token'); }}>
                התנתק
              </Button>
            </>
          ) : (
            <Button color="inherit" onClick={login}>התחבר</Button>
          )}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <TextField label="שם משתמש" value={username} onChange={(e) => setUsername(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <TextField label="סיסמה" type="password" value={password} onChange={(e) => setPassword(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <Button variant="contained" onClick={login}>התחבר</Button>
        </Box>
      ) : (
        <Box mt={3} display="flex" flexDirection="column">
          {/* ✅ מנהל רואה רק ניהול מערכת */}
          {role === "admin" ? (
            <Box sx={{ p: 3 }}>
              <Typography variant="h6">🔒 ניהול מערכת</Typography>
              <Typography>כאן יוצגו סטטיסטיקות ופעולות מנהל.</Typography>
            </Box>
          ) : (
            <>
              <Box display="flex" flexDirection="row">
                <Box flex={1} sx={{ p: 2, borderRight: "1px solid #ccc" }}>
                  <Typography variant="h6">📅 לוח שנה</Typography>
                  <Calendar onChange={setSelectedDate} value={selectedDate} />
                  {role === 'trainer' && (
                    <>
                      <TextField label="תיאור" value={description} onChange={(e) => setDescription(e.target.value)} fullWidth sx={{ mt: 2 }} />
                      <Button variant="contained" sx={{ mt: 2 }} onClick={addAppointment}>הוסף תזכורת</Button>
                    </>
                  )}
                  <Typography variant="h6" sx={{ mt: 2 }}>תזכורות קיימות:</Typography>
                  <ul>
                    {appointments.map((appt, index) => (
                      <li key={index}>{new Date(appt.date).toLocaleDateString()} - {appt.description}</li>
                    ))}
                  </ul>
                </Box>

                <Box flex={1} sx={{ p: 2, borderLeft: "1px solid #ccc" }}>
                  <Typography variant="h6">✅ רשימת משימות</Typography>
                  <ul>
                    {tasks.map((task, index) => (
                      <li key={index}>{task.description} {task.completed ? "✅" : "❌"}</li>
                    ))}
                  </ul>
                </Box>
              </Box>

              <Box sx={{ mt: 3, p: 2, borderTop: "1px solid #ccc" }}>
                <Typography variant="h6">💬 צ'אט</Typography>
                <TextField value={message} onChange={(e) => setMessage(e.target.value)} label="הקלד הודעה" fullWidth />
                <Button onClick={sendMessage} variant="contained" sx={{ mt: 1 }}>שלח</Button>
                <ul>
                  {chat.map((msg, index) => (
                    <li key={index}>{msg.user}: {msg.text}</li>
                  ))}
                </ul>
              </Box>
            </>
          )}
        </Box>
      )}
    </Container>
  );
}

export default App;
