import React, { useState } from "react";
import { Box, Paper, Typography } from "@mui/material";
import CalendarWidget from "./components/CalendarWidget";

function TrainerDashboard() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [tasks, setTasks] = useState([]); // רשימת משימות
  const isRTL = true; // נניח שהשפה היא עברית

  return (
    <Box display="flex" flexDirection={isRTL ? "row" : "row-reverse"} width="100%">
      
      {/* רשימת משימות */}
      <Paper
        sx={{
          flex: 1,
          height: "auto", // 🟢 התאמה דינמית לפי גודל לוח השנה
          margin: "10px",
          padding: "10px",
          overflowY: "auto",
          textAlign: "right", // 🟢 מיושר לימין
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.2)", // 🟢 הצללה יפה
          borderRadius: "10px", // 🟢 פינות מעוגלות
        }}
      >
        <Typography variant="h6">✅ משימות</Typography>
        {tasks.length === 0 ? (
          <Typography>אין משימות כרגע.</Typography>
        ) : (
          <ul>
            {tasks.map((task, index) => (
              <li key={index}>{task.description} {task.completed ? "✅" : "❌"}</li>
            ))}
          </ul>
        )}
      </Paper>

      {/* לוח שנה */}
      <CalendarWidget selectedDate={selectedDate} setSelectedDate={setSelectedDate} />

    </Box>
  );
}

export default TrainerDashboard;
