import React from "react";
import { Paper } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css"; // 🟢 קובץ CSS מותאם אישית (ניצור אותו בהמשך)

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  return (
    <Paper
      sx={{
        flex: 1,
        height: "550px", // 🟢 גודל קבוע שתואם למשימות
        margin: "10px",
        padding: "10px",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.2)", // 🟢 הצללה יפה
        borderRadius: "10px", // 🟢 פינות מעוגלות
      }}
    >
      <Calendar
        onChange={setSelectedDate}
        value={selectedDate}
        className="modern-calendar" // 🟢 מחלקה מותאמת אישית לעיצוב
      />
    </Paper>
  );
};

export default CalendarWidget;
