import React from "react";
import { Paper, Box } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css"; // ודא שהקובץ מיובא

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  return (
    <Paper
      sx={{
        flex: 1,
        width: "100%",
        height: "450px", // ✅ גובה קבוע מתאים למשימות
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "16px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      <Box sx={{ flexGrow: 1, width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          className="custom-calendar"
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
