import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, Paper, AppBar, Toolbar, Tabs, Tab, Container } from '@mui/material';

const socket = io('http://localhost:5000');
const [requireApproval, setRequireApproval] = useState(true); // האם יש צורך באישור מאלף?

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [tabIndex, setTabIndex] = useState(0); // ניווט בין כרטיסיות

  const [selectedFile, setSelectedFile] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [trainers, setTrainers] = useState([]);
const [originalUser, setOriginalUser] = useState(null); // לשמירת המשתמש המקורי

const fetchTrainers = async () => {
  try {
    const token = localStorage.getItem('token'); 
    if (!token) {
      console.error('🔴 לא נמצא טוקן, בדוק את ההתחברות.');
      return;
    }

    const response = await axios.get('http://localhost:5000/trainers', {
      headers: { Authorization: `Bearer ${token}` }
    });
    console.log("🔹 Trainers data:", response.data);
    setTrainers(response.data);
  } catch (error) {
    console.error('🔴 שגיאה בשליפת רשימת המאלפים:', error);
  }
};

const viewTrainer = (trainer) => {
  setOriginalUser(user); // שומר את המשתמש המקורי
  setUser(trainer); // מחליף את המשתמש הנוכחי בתצוגת המאלף
  setRole(trainer.role);
};

  const uploadFile = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('קובץ הועלה בהצלחה: ' + response.data.filePath);
    } catch (error) {
      console.error('שגיאה בהעלאת הקובץ:', error);
    }
  };

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:5000/chat/messages');
        setChat(response.data);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      }
    };

    fetchMessages();
    fetchTasks();

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

  }, []);

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      setUser(response.data.user);
      setRole(response.data.user.role);
      localStorage.setItem('token', response.data.token);
    } catch (error) {
      console.error('שגיאה בהתחברות:', error);
      alert('שם משתמש או סיסמה שגויים');
    }
  };

  const sendMessage = () => {
    socket.emit('sendMessage', { user: user.username, text: message });
    setMessage('');
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            מערכת ניהול משתמשים
          </Typography>
          {user && (
            <>
              <Typography variant="h6" sx={{ mr: 2 }}>שלום, {user.username}</Typography>
              <Button color="inherit" onClick={() => { 
                setUser(null); 
                setRole(null); 
                localStorage.removeItem('token'); 
              }}>
                התנתק
              </Button>
            </>
          )}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <TextField
            label="שם משתמש"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            fullWidth
            sx={{ mb: 2 }}
          />
          <TextField
            label="סיסמה"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            fullWidth
            sx={{ mb: 2 }}
          />
          <Button variant="contained" onClick={login}>
            התחבר
          </Button>
        </Box>
      ) : (
        <Box mt={3}>
          <Tabs value={tabIndex} onChange={(e, newValue) => setTabIndex(newValue)} centered>
  {role !== 'admin' && <Tab label="צ'אט" />}
  {role === 'trainer' && <Tab label="ניהול משימות" />}
  {role !== 'admin' && <Tab label="קבצים" />}
  {role === 'admin' && <Tab label="ניהול מערכת" />}
</Tabs>


<Paper sx={{ p: 3, mt: 3 }}>
  {/* תצוגה למנהל בלבד */}
  {role === 'admin' && tabIndex === 0 && (
  <>
    <Typography variant="h6">🔒 ניהול מערכת</Typography>
    <Typography>כאן יוצגו סטטיסטיקות ופעולות מנהל.</Typography>

    <Typography variant="h6" sx={{ mt: 3 }}>רשימת מאלפים</Typography>
    <ul>
      {trainers.map((trainer) => (
        <li key={trainer.username}>
          {trainer.username} - 
          <Button variant="outlined" sx={{ ml: 2 }} onClick={() => viewTrainer(trainer)}>
            צפה בתצוגת המאלף
          </Button>
        </li>
      ))}
    </ul>
  </>
)}

  {/* צ'אט - רק למשתמשים שאינם אדמין */}
  {role !== 'admin' && tabIndex === 0 && (
    <>
      <Typography variant="h6">צ'אט</Typography>
      <TextField value={message} onChange={(e) => setMessage(e.target.value)} label="הקלד הודעה" fullWidth />
      <Button onClick={sendMessage} variant="contained" sx={{ mt: 1 }}>
        שלח
      </Button>
      <ul>
        {chat.map((msg, index) => (
          <li key={index}>{msg.user}: {msg.text}</li>
        ))}
      </ul>
    </>
  )}

  {/* ניהול משימות - רק למאלף */}
  {role === 'trainer' && tabIndex === 1 && (
    <>
      <Typography variant="h6">רשימת משימות</Typography>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            {task.description} {task.completed ? "✅" : "❌"}
          </li>
        ))}
      </ul>
    </>
  )}

  {/* ניהול קבצים - רק ללקוחות ולמאלפים */}
  {role !== 'admin' && tabIndex === 2 && (
    <>
      <Typography variant="h6">ניהול קבצים</Typography>
      <input type="file" onChange={(e) => setSelectedFile(e.target.files[0])} />
      <Button onClick={uploadFile} variant="contained" sx={{ mt: 1 }}>
        העלה קובץ
      </Button>
    </>
  )}
</Paper>
        </Box>
      )}
    </Container>
  );
}

export default App;
