import React from "react";
import { Paper } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

const CalendarWidget = () => {
  return (
    <Box display="flex" flexDirection={isRTL ? "row-reverse" : "row"} width="100%">
  
  {/* לוח שנה */}
  <Paper sx={{
    flex: 1,
    height: "500px", // 🟢 גודל אחיד לתיבה
    margin: "10px",
    padding: "10px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  }}>
    <CalendarWidget selectedDate={selectedDate} setSelectedDate={setSelectedDate} />
  </Paper>

  {/* רשימת משימות */}
  <Paper sx={{
    flex: 1,
    height: "500px", // 🟢 גובה זהה ללוח השנה
    margin: "10px",
    padding: "10px",
    overflowY: "auto",
    textAlign: "right" // 🟢 מיושר לימין
  }}>
    <Typography variant="h6">✅ משימות</Typography>
    {tasks.length === 0 ? (
      <Typography>אין משימות כרגע.</Typography>
    ) : (
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>{task.description} {task.completed ? "✅" : "❌"}</li>
        ))}
      </ul>
    )}
  </Paper>
</Box>
  );
};

export default CalendarWidget;
