import React, { useState, useEffect } from "react";
import { Paper, TextField, Button, Typography, Box, List, ListItem, ListItemText, Checkbox, IconButton } from "@mui/material";
import axios from "axios";
import DeleteIcon from '@mui/icons-material/Delete';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  // טעינת משימות מהשרת
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get("http://localhost:5000/tasks");
        setTasks(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בטעינת המשימות:", error);
      }
    };
    fetchTasks();
  }, []);

  // הוספת משימה חדשה
  const addTask = async () => {
    if (!newTask.trim()) return; // לא מוסיפים משימה ריקה

    try {
      const response = await axios.post("http://localhost:5000/tasks", { description: newTask, completed: false });
      setTasks([...tasks, response.data]);
      setNewTask(""); // איפוס השדה לאחר הוספה
    } catch (error) {
      console.error("🔴 שגיאה בהוספת משימה:", error);
    }
  };

  // סימון משימה כבוצעה ✅
  const markTaskCompleted = async (taskId) => {
    try {
      await axios.put(`http://localhost:5000/tasks/${taskId}`, { completed: true });
      setTasks(tasks.map(task => task._id === taskId ? { ...task, completed: true } : task));
    } catch (error) {
      console.error("🔴 שגיאה בעדכון המשימה:", error);
    }
  };

  // מחיקת משימה ❌
  const deleteTask = async (taskId) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${taskId}`);
      setTasks(tasks.filter(task => task._id !== taskId));
    } catch (error) {
      console.error("🔴 שגיאה במחיקת המשימה:", error);
    }
  };

  return (
    <Paper sx={{
      flex: 1,
      height: "500px",
      margin: "10px",
      padding: "10px",
      overflowY: "auto",
      textAlign: "right"
    }}>
      <Typography variant="h6">✅ משימות</Typography>

      {/* שדה הוספת משימה */}
      <Box display="flex" sx={{ mt: 2 }}>
        <TextField
          fullWidth
          label="הוסף משימה חדשה"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <Button variant="contained" onClick={addTask} sx={{ ml: 1 }}>➕</Button>
      </Box>

      {/* רשימת משימות */}
      <List sx={{ mt: 2 }}>
        {tasks.length === 0 ? (
          <Typography>אין משימות כרגע.</Typography>
        ) : (
          tasks.map((task) => (
            <ListItem key={task._id} sx={{ display: "flex", justifyContent: "space-between" }}>
              <ListItemText
                primary={task.description}
                sx={{ textDecoration: task.completed ? "line-through" : "none" }}
              />
              <Checkbox
                checked={task.completed}
                onChange={() => markTaskCompleted(task._id)}
              />
              <IconButton onClick={() => deleteTask(task._id)} color="error">
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))
        )}
      </List>
    </Paper>
  );
};

export default TaskList;
