import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, AppBar, Toolbar, Container, CircularProgress } from '@mui/material';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [description, setDescription] = useState('');
  const [trainers, setTrainers] = useState([]);
  const [originalUser, setOriginalUser] = useState(null);
  const [pendingRequest, setPendingRequest] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:5000/chat/messages');
        setChat(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת הודעות:", error);
      }
    };

    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת משימות:", error);
      }
    };

    const fetchAppointments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/appointments', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setAppointments(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת תזכורות:", error);
      }
    };

    const fetchTrainers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/trainers', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setTrainers(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת רשימת המאלפים:", error);
      }
    };

    fetchMessages();
    fetchTasks();
    if (role !== 'admin') {
      fetchAppointments();
    }
    if (role === 'admin') {
      fetchTrainers();
    }

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    socket.on("notifyTrainer", (data) => {
      if (role === "trainer" && data.trainer === user.username) {
        setPendingRequest(data.admin);
      }
    });

  }, [role]);

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      setUser(response.data.user);
      setRole(response.data.user.role);
      localStorage.setItem('token', response.data.token);
    } catch (error) {
      console.error('🔴 שגיאה בהתחברות:', error);
      alert('שם משתמש או סיסמה שגויים');
    }
  };

  const sendMessage = () => {
    socket.emit('sendMessage', { user: user.username, text: message });
    setMessage('');
  };

  const addAppointment = async () => {
    if (role !== 'trainer') return;
    try {
      await axios.post('http://localhost:5000/appointments', {
        date: selectedDate,
        client: user.username,
        description
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setAppointments([...appointments, { date: selectedDate, client: user.username, description }]);
      setDescription('');
    } catch (error) {
      console.error("🔴 שגיאה בהוספת תזכורת:", error);
    }
  };

  const viewTrainer = (trainer) => {
    setIsLoading(true); // ✅ הפעלת מצב טעינה

    socket.emit("requestView", { admin: user.username, trainer: trainer.username });

    const timeout = setTimeout(() => {
      setIsLoading(false); // ✅ ביטול מצב טעינה לאחר 10 שניות
      alert("⏳ הבקשה פגה תוקף, נסה שוב.");
      socket.off("viewResponse");
    }, 10000);

    socket.on("viewResponse", (response) => {
      clearTimeout(timeout); // ✅ ביטול הטיימר אם קיבלנו תגובה
      setIsLoading(false); // ✅ ביטול מצב טעינה
      if (response.approved) {
        setOriginalUser(user);
        setUser(trainer);
        setRole(trainer.role);
      } else {
        alert("❌ המאלף דחה את הבקשה לצפות בתצוגתו.");
      }
    });
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>🐾 מערכת ניהול מאלפים</Typography>
          <Button color="inherit">דף הבית</Button>
          <Button color="inherit">פורום</Button>
          <Button color="inherit">מצא מאלף</Button>
          {user ? (
            <>
              <Typography variant="h6" sx={{ mx: 2 }}>שלום, {user.username}</Typography>
              <Button color="inherit" onClick={() => { setUser(null); setRole(null); localStorage.removeItem('token'); }}>
                התנתק
              </Button>
            </>
          ) : (
            <Button color="inherit" onClick={login}>התחבר</Button>
          )}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <TextField label="שם משתמש" value={username} onChange={(e) => setUsername(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <TextField label="סיסמה" type="password" value={password} onChange={(e) => setPassword(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <Button variant="contained" onClick={login}>התחבר</Button>
        </Box>
      ) : (
        <Box mt={3}>
          {role === "admin" ? (
            <Box sx={{ p: 3 }}>
              <Typography variant="h6">🔒 ניהול מערכת</Typography>
              <Typography>כאן יוצגו סטטיסטיקות ופעולות מנהל.</Typography>
              <Typography variant="h6" sx={{ mt: 3 }}>רשימת מאלפים</Typography>
              <ul>
                {trainers.map((trainer) => (
                  <li key={trainer.username}>
                    {trainer.username} - 
                    <Button 
                      variant="outlined" 
                      sx={{ ml: 2 }} 
                      onClick={() => viewTrainer(trainer)} 
                      disabled={isLoading}
                    >
                      {isLoading ? <CircularProgress size={20} /> : "צפה בתצוגת המאלף"}
                    </Button>
                  </li>
                ))}
              </ul>
            </Box>
          ) : (
            <Box> {/* כאן מופיע התוכן של לוח השנה, משימות וצ'אט */} </Box>
          )}
        </Box>
      )}
    </Container>
  );
}

export default App;
