import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, Paper, AppBar, Toolbar, Tabs, Tab, Container } from '@mui/material';

const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [tabIndex, setTabIndex] = useState(0);

  const [selectedFile, setSelectedFile] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [trainers, setTrainers] = useState([]);
  const [originalUser, setOriginalUser] = useState(null);
  const [requireApproval, setRequireApproval] = useState(true);
  const [pendingRequest, setPendingRequest] = useState(null);

  const fetchTrainers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/trainers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setTrainers(response.data);
    } catch (error) {
      console.error('שגיאה בשליפת רשימת המאלפים:', error);
    }
  };

  const viewTrainer = (trainer) => {
    if (!requireApproval) {
      setOriginalUser(user);
      setUser(trainer);
      setRole(trainer.role);
      return;
    }

    socket.emit("requestView", { admin: user.username, trainer: trainer.username });

    socket.on("viewResponse", (response) => {
      if (response.approved) {
        setOriginalUser(user);
        setUser(trainer);
        setRole(trainer.role);
      } else {
        alert("המאלף דחה את הבקשה לצפות בתצוגתו.");
      }
    });

    setTimeout(() => {
      socket.off("viewResponse");
    }, 10000);
  };

  const uploadFile = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('קובץ הועלה בהצלחה: ' + response.data.filePath);
    } catch (error) {
      console.error('שגיאה בהעלאת הקובץ:', error);
    }
  };

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:5000/chat/messages');
        setChat(response.data);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      }
    };

    fetchMessages();
    fetchTasks();

    if (role === 'admin') {
      fetchTrainers();
    }

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    socket.on("notifyTrainer", (data) => {
      if (role === "trainer" && data.trainer === user.username) {
        setPendingRequest(data.admin);
      }
    });

  }, [role, user]);

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      setUser(response.data.user);
      setRole(response.data.user.role);
      localStorage.setItem('token', response.data.token);
    } catch (error) {
      console.error('שגיאה בהתחברות:', error);
      alert('שם משתמש או סיסמה שגויים');
    }
  };

  const sendMessage = () => {
    socket.emit('sendMessage', { user: user.username, text: message });
    setMessage('');
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            מערכת ניהול משתמשים
          </Typography>
          {user && (
            <>
              <Typography variant="h6" sx={{ mr: 2 }}>שלום, {user.username}</Typography>

              {originalUser && (
                <Button color="inherit" onClick={() => { 
                  setUser(originalUser);
                  setRole(originalUser.role);
                  setOriginalUser(null);
                }}>
                  חזור למצב מנהל
                </Button>
              )}

              <Button color="inherit" onClick={() => { 
                setUser(null); 
                setRole(null); 
                localStorage.removeItem('token'); 
              }}>
                התנתק
              </Button>
            </>
          )}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <TextField label="שם משתמש" value={username} onChange={(e) => setUsername(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <TextField label="סיסמה" type="password" value={password} onChange={(e) => setPassword(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <Button variant="contained" onClick={login}>התחבר</Button>
        </Box>
      ) : (
        <Box mt={3}>
          {role === "trainer" && pendingRequest && (
            <Paper sx={{ p: 2, mt: 3, backgroundColor: "#f8d7da" }}>
              <Typography variant="h6">{pendingRequest} רוצה לצפות בתצוגתך. לאשר?</Typography>
              <Button variant="contained" color="success" sx={{ mr: 2 }}
                onClick={() => { socket.emit("viewResponse", { approved: true }); setPendingRequest(null); }}>
                אשר
              </Button>
              <Button variant="contained" color="error"
                onClick={() => { socket.emit("viewResponse", { approved: false }); setPendingRequest(null); }}>
                דחה
              </Button>
            </Paper>
          )}
        </Box>
      )}
    </Container>
  );
}

export default App;
