import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField, Box, AppBar, Toolbar, Container, CircularProgress, FormControlLabel, Checkbox } from '@mui/material';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [description, setDescription] = useState('');
  const [trainers, setTrainers] = useState([]);
  const [originalUser, setOriginalUser] = useState(null);
  const [pendingRequest, setPendingRequest] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUser, setTypingUser] = useState(null);
  const [isGroupMessage, setIsGroupMessage] = useState(false);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:5000/chat/messages');
        setChat(response.data);
      } catch (error) {
        console.error("🔴 שגיאה בשליפת הודעות:", error);
      }
    };

    fetchMessages();

    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    socket.on("userTyping", (data) => {
      setTypingUser(data.user);
      setTimeout(() => setTypingUser(null), 3000);
    });

  }, []);

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      setUser(response.data.user);
      setRole(response.data.user.role);
      localStorage.setItem('token', response.data.token);
    } catch (error) {
      console.error('🔴 שגיאה בהתחברות:', error);
      alert('שם משתמש או סיסמה שגויים');
    }
  };

  const handleTyping = () => {
    socket.emit("typing", { user: user.username });
  };

  const sendMessage = () => {
    socket.emit('sendMessage', { 
      user: user.username, 
      text: message, 
      group: isGroupMessage 
    });
    setMessage('');
  };

  const editMessage = (index) => {
    const newMessage = prompt("ערוך את ההודעה שלך:", chat[index].text);
    if (newMessage) {
      const updatedChat = [...chat];
      updatedChat[index].text = newMessage;
      setChat(updatedChat);
    }
  };

  const deleteMessage = (index) => {
    if (window.confirm("האם אתה בטוח שברצונך למחוק את ההודעה?")) {
      const updatedChat = [...chat];
      updatedChat.splice(index, 1);
      setChat(updatedChat);
    }
  };

  return (
    <Container>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>🐾 מערכת ניהול מאלפים</Typography>
          <Button color="inherit">דף הבית</Button>
          <Button color="inherit">פורום</Button>
          <Button color="inherit">מצא מאלף</Button>
          {user ? (
            <>
              <Typography variant="h6" sx={{ mx: 2 }}>שלום, {user.username}</Typography>
              <Button color="inherit" onClick={() => { setUser(null); setRole(null); localStorage.removeItem('token'); }}>
                התנתק
              </Button>
            </>
          ) : (
            <Button color="inherit" onClick={login}>התחבר</Button>
          )}
        </Toolbar>
      </AppBar>

      {!user ? (
        <Box textAlign="center" mt={5}>
          <TextField label="שם משתמש" value={username} onChange={(e) => setUsername(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <TextField label="סיסמה" type="password" value={password} onChange={(e) => setPassword(e.target.value)} fullWidth sx={{ mb: 2 }} />
          <Button variant="contained" onClick={login}>התחבר</Button>
        </Box>
      ) : (
        <Box mt={3}>
          <Box sx={{ mt: 3, p: 2, borderTop: "1px solid #ccc" }}>
            <Typography variant="h6">💬 צ'אט</Typography>
            <TextField 
              value={message} 
              onChange={(e) => { setMessage(e.target.value); handleTyping(); }} 
              label="הקלד הודעה" 
              fullWidth 
            />
            <FormControlLabel 
              control={<Checkbox checked={isGroupMessage} onChange={() => setIsGroupMessage(!isGroupMessage)} />} 
              label="שלח הודעה קבוצתית" 
            />
            <Button onClick={sendMessage} variant="contained" sx={{ mt: 1 }}>שלח</Button>
            {typingUser && <Typography sx={{ color: "gray", mt: 1 }}>{typingUser} מקליד...</Typography>}
            <ul>
              {chat.map((msg, index) => (
                <li key={index}>
                  {msg.user}: {msg.text} 
                  {msg.user === user.username && (
                    <>
                      <Button size="small" onClick={() => editMessage(index)}>✏ ערוך</Button>
                      <Button size="small" onClick={() => deleteMessage(index)}>🗑 מחק</Button>
                    </>
                  )}
                </li>
              ))}
            </ul>
          </Box>
        </Box>
      )}
    </Container>
  );
}

export default App;
