import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import { Button, Typography, TextField } from '@mui/material';

const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);

  useEffect(() => {
    socket.on('receiveMessage', (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });
  }, []);

  const login = async () => {
    const response = await axios.post('http://localhost:5000/login', { username: 'testUser', password: '123456' });
    setUser(response.data.user);
  };

  const sendMessage = () => {
    socket.emit('sendMessage', { user: user.username, text: message });
    setMessage('');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>מערכת ניהול משתמשים</h1>
      {!user ? (
        <Button variant='contained' onClick={login}>התחבר</Button>
      ) : (
        <div>
          <Typography variant='h5'>שלום, {user.username}</Typography>
          <TextField value={message} onChange={(e) => setMessage(e.target.value)} label='הקלד הודעה' />
          <Button onClick={sendMessage}>שלח</Button>
          <ul>
            {chat.map((msg, index) => (
              <li key={index}>{msg.user}: {msg.text}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
