import React, { useState } from "react";
import { Paper, Box, Button } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  const [currentDate, setCurrentDate] = useState(new Date());

  const handleTodayClick = () => {
    setSelectedDate(new Date());
  };

  return (
    <Paper
      sx={{
        flex: 1,
        width: "100%",
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "12px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      {/* סרגל עליון עם שם החודש, כפתורי ניווט וכפתור חזרה להיום */}
      <Box sx={{ width: "100%", display: "flex", alignItems: "center", justifyContent: "space-between", background: "#4CAF50", padding: "8px", borderRadius: "8px" }}>
        <Button onClick={handleTodayClick} variant="contained" color="secondary" sx={{ fontSize: "14px", padding: "5px 10px" }}>
          חזרה להיום
        </Button>
        <h3 style={{ margin: 0, color: "#fff" }}>
          {currentDate.toLocaleString("he-IL", { month: "long", year: "numeric" })}
        </h3>
      </Box>

      <Box sx={{ width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          locale="he-IL"
          showNavigation={true}
          calendarType="hebrew"
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
