import React, { useState } from "react";
import { Paper, Box, Button } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "../styles/CalendarStyles.css";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  // חזרה לתאריך של היום
  const goToToday = () => {
    setSelectedDate(new Date());
  };

  // בחירת טווח תאריכים
  const handleDateClick = (date) => {
    if (!startDate) {
      setStartDate(date);
      setEndDate(null);
    } else if (!endDate) {
      setEndDate(date);
    } else {
      setStartDate(date);
      setEndDate(null);
    }
  };

  // פונקציה לסימון טווח התאריכים
  const tileClassName = ({ date, view }) => {
    if (view === "month" && startDate && endDate) {
      if (date >= startDate && date <= endDate) {
        return "highlight-range";
      }
    }
    return "";
  };

  return (
    <Paper
      sx={{
        flex: 1,
        width: "100%",
        height: "400px",
        padding: "10px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "12px",
        background: "#ffffff",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
      }}
    >
      {/* שורת ניווט עם כפתור "חזרה להיום" */}
      <Box
        sx={{
          width: "100%",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "#4CAF50",
          color: "white",
          padding: "5px 15px",
          borderRadius: "8px",
        }}
      >
        <span>{selectedDate.toLocaleString("he-IL", { month: "long", year: "numeric" })}</span>
        <Button
          variant="contained"
          color="secondary"
          size="small"
          onClick={goToToday}
        >
          חזרה להיום
        </Button>
      </Box>

      {/* לוח שנה */}
      <Box sx={{ width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          onClickDay={handleDateClick}
          tileClassName={tileClassName}
          locale="he-IL"
          calendarType="hebrew"
          firstDayOfWeek={0} // מתחיל ביום ראשון
        />
      </Box>
    </Paper>
  );
};

export default CalendarWidget;
