import React from "react";
import { Paper } from "@mui/material";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

const CalendarWidget = ({ selectedDate, setSelectedDate }) => {
  return (
    <Paper sx={{
      width: "100%",  
      height: "400px",  // ✅ גובה קבוע - כדי שלא יגדל יותר מדי
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      borderRadius: "12px",
      boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)", // ✅ הופך את זה ליותר מודרני
      padding: "10px",
      background: "#f9f9f9"
    }}>
      <Calendar
        onChange={setSelectedDate}
        value={selectedDate}
        tileClassName={({ date }) => {
          return date.getDate() === selectedDate.getDate() ? "selected-date" : "";
        }}
      />
    </Paper>
  );
};

export default CalendarWidget;
